import React, { Component } from 'react';
import { Input, Button, Card, Select, Alert } from 'antd';
import $ from "jquery";
import 'antd/dist/antd.css';

const { Option } = Select;

class CreateMonitor extends React.Component {
  constructor() {
    super();
    this.state = {
        validate: '',
        type: '',
        urlDone: false,
        msg: '',
        url: '',
        range: '',
        email: '',
        phone: ''
    };
  }

  customAjax = (url, callback) => {
    $.ajax({
        type: "POST",
        url: "http://localhost/CodeIgniter-2.2.6/CodeIgniter-2.2.6/index.php/welcome/signup",
        dataType: 'json',
        data: {
        },
        success: (json) => {
        }
    });
  }

  createCron = () => {
      console.log("clicked");
    if(document.getElementById('url')) {
        this.setState({
            validate: true,
            msg: 'Enter url',
            type: 'error'
        })
    }
  }

  openDialog = () => {
    this.setState({
        urlDone: true,
    })
  }

  render() {
    return (
       <div style={{"textAlign": "center", "width": '40%'}}>
            <center>
                {this.state.validate ? <Alert message={this.state.msg} type={this.state.type} banner/> : ''}
                <Input id="url" placeholder="Enter URL" />
                <Select id="range" defaultValue="1" style={{ width: 120 }}>
                    <Option value="1">10 Sec</Option>
                    <Option value="2">1 Min</Option>
                    <Option value="3">5 Min</Option>
                    <Option value="4">10 Min</Option>
                    <Option value="5">15 Min</Option>
                    <Option value="6">1 Hour</Option>
                    <Option value="7">4 Hour</Option>
                    <Option value="8">6 Hour</Option>
                    <Option value="9">Daily</Option>
                </Select>
                <Button type="primary" onClick={this.openDialog}>Contact Details</Button>
                {this.state.urlDone ? <div>
                    <Card style={{ width: '60%' }}>
                        <div style={{"textAlign": "center"}}>
                            <Input id="email" placeholder="Enter Email" />
                            <Input id="phone" placeholder="Enter Phone" />
                            <Button type="primary" onClick={this.createCron}>Create Cron</Button>
                        </div>
                    </Card>
                </div>: ''}
            </center>
       </div>
    );
  }
}

export default CreateMonitor;
